
public class Strings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(">>>String Examples<<<");
		
		String text="Hello";
		String space=" ";
		String name="Ashutosh";
		
		System.out.println(text);
		System.out.println(text+space+name);
		System.out.println("Hello"+" "+"Ashutosh");
		
		int mynumber=27;
		double mydouble=10.7;
		
		System.out.println("My numbers are: "+mynumber+" "+mydouble);
		
	}

}
