
public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=0;i<5;i++)
		{
			//System.out.println("The value of i is:"+i);
			System.out.printf("The value of i is: %d\n", i);// 
			/*%d - format specifier - It begins with % and end in a letter(any)
			%d means print the string but replace %d with the value of integer specified here, 
			after the comma every time */
			
		}

	
	}

}
